properties.script:
try
{
	var id = UUIDGenerator.getUUID();
	var versionId = '1';

	var data = AttachmentUtil.reAttachMessage(connectorMessage);

	if($('contentType') == 'application/fhir+xml' || $('contentType') == 'application/xml+fhir;charset=utf-8')
	{
		data = fhirConvertResource(data, 'xml', 'json');
	}

	var docData = com.mongodb.DBObject ( com.mongodb.util.JSON.parse( data ));

	$c('resourceToStore', data);

	var db = get_mongo_conn(); // code template

	coll = db.getCollection($('fhirType'));
	var doc = com.mongodb.BasicDBObject ();
	doc.put('id', id);
	doc.put('versionId', versionId);
	doc.put('deleted', '0');
	doc.put('data', docData);

	coll.insert(doc);
	//var id = org.bson.types.ObjectId ( doc.get( '_id' ) );
	responseMap.put('response', FhirResponseFactory.getCreateResponse(id, versionId, 201));
}
catch(e)
{
	responseMap.put('response', FhirResponseFactory.getCreateResponse(id, versionId, 500));	
	throw e;
}

var versionId = $('fhirVid');
if(versionId <= '')
	versionId = '1';

var type = $('fhirType');
var id = $('fhirId');

var data = AttachmentUtil.reAttachMessage(connectorMessage);

if($('contentType') == 'application/fhir+xml')
{
	data = fhirConvertResource(data, 'xml', 'json');
}

var docData = com.mongodb.DBObject ( com.mongodb.util.JSON.parse( data ));

$c('resourceToStore', data);

var db = get_mongo_conn(); // code template

coll = db.getCollection($('fhirType'));

var newDocument = new com.mongodb.BasicDBObject();
newDocument.append("$set", new com.mongodb.BasicDBObject().append("data", docData));

var searchQuery = new com.mongodb.BasicDBObject().append("id", id).append("versionId", versionId);

var writeRes = coll.update(searchQuery, newDocument,true,false);
var result = writeRes.getN();
var isUpdate = writeRes.isUpdateOfExisting();

var responseCode;

if (result == 1 && !isUpdate) {
	responseCode = 201;
} else if (result == 1 && isUpdate) {
	responseCode = 200;
}
else if(result == 0)
	responseCode = 404;
else
	responseCode = 400;

responseMap.put('response', FhirResponseFactory.getUpdateResponse(versionId, responseCode));

var versionId = $('vid');
if(versionId <= '')
	versionId = '1';

var type = $('fhirType');
var id = $('fhirId');

var db = get_mongo_conn(); // code template

coll = db.getCollection($('fhirType'));

var newDocument = new com.mongodb.BasicDBObject();
newDocument.append("$set", new com.mongodb.BasicDBObject().append("deleted", '1'));

var searchQuery = new com.mongodb.BasicDBObject().append("id", id).append("versionId", versionId);

var writeRes = coll.update(searchQuery, newDocument);
var result = writeRes.getN();
var isUpdate = writeRes.isUpdateOfExisting();

var responseCode;

if (result == 1 && isUpdate) {
	responseCode = 204;
}
else if(result == 0)
	responseCode = 404;
else
	responseCode = 400;

responseMap.put('response', FhirResponseFactory.getResponse(null, responseCode));

	var db = get_mongo_conn(); // code template

	coll = db.getCollection($('fhirType'));

	var query = new com.mongodb.BasicDBObject().append("id", $('fhirId'));
	var fields = new com.mongodb.BasicDBObject().append("data", 1).append("versionId", 1).append("_id", 0);
	var cursor = coll.find(query, fields);

	var response;

	if(cursor.hasNext()) {
       	var obj = cursor.next();
       	var versionId = obj.get('versionId').toString();
       	var data = obj.get('data');

       	var resJson = com.mongodb.util.JSON.serialize(data);
		$c('resJson', resJson);

		response = FhirResponseFactory.getReadResponse(data, versionId, null, 200, 'application/fhir+json');
    	}
   	else {
		response = FhirResponseFactory.getReadResponse(null, null, 410, null);
	}

	responseMap.put('response', response);

	var db = get_mongo_conn(); // code template

	coll = db.getCollection($('fhirType'));

	var query = new com.mongodb.BasicDBObject().append("id", $('fhirId')).append("versionId", $('fhirVid'));
	var fields = new com.mongodb.BasicDBObject().append("data", 1).append("_id", 0);
	var cursor = coll.find(query, fields);

	var response;

	if(cursor.hasNext()) {
       	var obj = cursor.next();
       	var data = obj.get('data');

       	var resJson = com.mongodb.util.JSON.serialize(data);
		$c('resJson', resJson);

		response = FhirResponseFactory.getVreadResponse(data, 200, 'application/fhir+json');
    	}
   	else {
		response = FhirResponseFactory.getVreadResponse('', 410, null);
	}

	responseMap.put('response', response);

