entry.string:
// Modify the message variable below to create attachments

var fhirType = $('fhirType');
var fhirInteraction = $('fhirInteraction');

// If we're creating or updating a Binary resource, add an attachment for it
if (fhirType == 'Binary' && (fhirInteraction == 'create' || fhirInteraction == 'update')) {
	return addAttachment(message, $('headers').getHeader('Content-Type')).getAttachmentId();
}

return message;
